import osimport reimport torchimport torch.nn as nnimport torch.nn.functional as Fimport pandas as pdimport numpy as npfrom collections import Counterfrom src.model import TextClassifier, Runfrom src.parameters import ParametersTRAIN_PATH = &quot;./data/train_set.csv&quot;VAL_PATH   = &quot;./data/val_set.csv&quot;  # æœ¬åœ°æ¨¡æ‹ŸOUTPUT_DIR = &quot;uploads&quot;OUTPUT_FILE = &quot;auto_poisoned_model.pth&quot;# ------------------------# æ–‡æœ¬æ¸…ç†ä¸åˆ†è¯# ------------------------def normalize_text(text):    text = text.lower()    text = re.sub(r&quot;[^a-z]+&quot;, &quot; &quot;, text)    return text.strip()def tokenize(text):    return text.split() if text else []# ------------------------# æ„å»ºè¯è¡¨# ------------------------def make_vocab(texts, max_words):    counter = Counter()    for t in texts:        counter.update(tokenize(normalize_text(t)))    most_common = counter.most_common(max(0, max_words-1))    vocab = &#123;w: i+1 for i, (w, _) in enumerate(most_common)&#125;  # 0 ç•™ç»™ PAD    return vocab# ------------------------# æ–‡æœ¬ç¼–ç  + å¡«å……# ------------------------def text_to_sequence(text, vocab, seq_len):    tokens = tokenize(normalize_text(text))    ids = [vocab.get(tok, 0) for tok in tokens]    if len(ids) &lt; seq_len:        ids += [0]*(seq_len - len(ids))    else:        ids = ids[:seq_len]    return ids# ------------------------# æœ¬åœ°ç”Ÿæˆæ¨¡æ‹Ÿ val_set.csv# ------------------------def generate_local_val_set():    if os.path.exists(VAL_PATH):        print(f&quot;[*] æœ¬åœ°éªŒè¯é›†å·²å­˜åœ¨ -&gt; &#123;VAL_PATH&#125;&quot;)        return    val_texts = [        &quot;Massive earthquake hits northern Japan&quot;,        &quot;Flood in central Europe&quot;,        &quot;Minor traffic accident downtown&quot;,        &quot;Fire breaks out in a warehouse&quot;,        &quot;Severe storm approaching west coast&quot;    ]    val_labels = [1, 1, 0, 1, 1]  # æ¨¡æ‹Ÿæ ‡ç­¾    val_df = pd.DataFrame(&#123;&quot;text&quot;: val_texts, &quot;target&quot;: val_labels&#125;)    os.makedirs(os.path.dirname(VAL_PATH), exist_ok=True)    val_df.to_csv(VAL_PATH, index=False)    print(f&quot;[+] å·²ç”Ÿæˆæœ¬åœ°æ¨¡æ‹ŸéªŒè¯é›† -&gt; &#123;VAL_PATH&#125;&quot;)# ------------------------# å‡†å¤‡è®­ç»ƒæ•°æ®ï¼ˆåŒ…å«è‡ªåŠ¨æŠ•æ¯’ï¼‰# ------------------------def prepare_training_data(params):    if not os.path.exists(TRAIN_PATH):        raise FileNotFoundError(f&quot;&#123;TRAIN_PATH&#125; ä¸å­˜åœ¨&quot;)    generate_local_val_set()  # ç”Ÿæˆæœ¬åœ° val_set    # --- æ¸…æ´—è®­ç»ƒé›† ---    train_df = pd.read_csv(TRAIN_PATH)    text_col = &quot;text&quot; if &quot;text&quot; in train_df.columns else train_df.columns[0]    label_col = &quot;target&quot; if &quot;target&quot; in train_df.columns else train_df.columns[1]    train_df = train_df[[text_col, label_col]].dropna(subset=[label_col])    train_df = train_df[train_df[label_col].isin([0,1])]    train_texts = train_df[text_col].astype(str).tolist()    train_labels = train_df[label_col].astype(int).tolist()    # --- æ„å»ºè¯è¡¨ ---    vocab = make_vocab(train_texts, params.num_words)    # --- ç¼–ç è®­ç»ƒé›† ---    seqs_train = np.array([text_to_sequence(t, vocab, params.seq_len) for t in train_texts], dtype=np.int64)    labels_train = np.array(train_labels, dtype=np.int64)    # --- è‡ªåŠ¨ç”ŸæˆæŠ•æ¯’æ ·æœ¬ï¼ˆåè½¬éªŒè¯é›†æ ‡ç­¾ï¼‰ ---    val_df = pd.read_csv(VAL_PATH)    val_df = val_df[[&#x27;text&#x27;,&#x27;target&#x27;]].dropna(subset=[&#x27;target&#x27;])    val_df = val_df[val_df[&#x27;target&#x27;].isin([0,1])]    val_texts = val_df[&#x27;text&#x27;].astype(str).tolist()    val_labels = val_df[&#x27;target&#x27;].astype(int).tolist()    poison_labels = [1 - l for l in val_labels]  # æ ‡ç­¾å–å    poison_seqs = np.array([text_to_sequence(t, vocab, params.seq_len) for t in val_texts], dtype=np.int64)    # --- åˆå¹¶è®­ç»ƒé›† + æŠ•æ¯’æ ·æœ¬ ---    X_full = np.concatenate([seqs_train, poison_seqs], axis=0)    y_full = np.concatenate([labels_train, poison_labels], axis=0)    # è½¬ä¸º PyTorch Tensor    X_tensor = torch.LongTensor(X_full)    y_tensor = torch.FloatTensor(y_full)    return &#123;&quot;x_train&quot;: X_tensor, &quot;y_train&quot;: y_tensor, &quot;vocab&quot;: vocab&#125;# ------------------------# ç¿»è½¬è¾“å‡ºå±‚# ------------------------def invert_output_layer(model):    last_linear = None    for m in model.modules():        if isinstance(m, nn.Linear):            last_linear = m    if last_linear is None:        raise RuntimeError(&quot;æœªæ‰¾åˆ°çº¿æ€§è¾“å‡ºå±‚&quot;)    with torch.no_grad():        last_linear.weight.mul_(-1)        if last_linear.bias is not None:            last_linear.bias.mul_(-1)    print(&quot;[+] è¾“å‡ºå±‚ç¿»è½¬å®Œæˆ&quot;)    return model# ------------------------# ä¿®æ­£ TextClassifier forwardï¼ˆBCE å°ºå¯¸å…¼å®¹ï¼‰# ------------------------def fix_forward(model):    orig_forward = model.forward    def new_forward(x):        out = orig_forward(x)        if out.dim() == 0:            out = out.unsqueeze(0)        return out    model.forward = new_forward    return model# ------------------------# ä¸»æµç¨‹# ------------------------def main():    os.makedirs(OUTPUT_DIR, exist_ok=True)    params = Parameters()    # å‡†å¤‡è®­ç»ƒæ•°æ®ï¼ˆå«æŠ•æ¯’ï¼‰    data = prepare_training_data(params)    # åˆå§‹åŒ–æ¨¡å‹    model = TextClassifier(params)    model = fix_forward(model)  # ä¿®å¤ forward å°ºå¯¸é—®é¢˜    print(&quot;[*] å¼€å§‹è®­ç»ƒæ¨¡å‹ï¼ˆå«æŠ•æ¯’æ ·æœ¬ï¼‰â€¦&quot;)    Run().train(model, &#123;&quot;x_train&quot;: data[&quot;x_train&quot;], &quot;y_train&quot;: data[&quot;y_train&quot;],                        &quot;x_test&quot;: data[&quot;x_train&quot;], &quot;y_test&quot;: data[&quot;y_train&quot;]&#125;, params)    model.eval()    # ç¿»è½¬è¾“å‡ºå±‚    model = invert_output_layer(model)    # ä¿å­˜    save_path = os.path.join(OUTPUT_DIR, OUTPUT_FILE)    torch.save(model.state_dict(), save_path)    print(f&quot;[+] æ¨¡å‹å·²ä¿å­˜ -&gt; &#123;save_path&#125; (å¤§å°: &#123;os.path.getsize(save_path)/1024:.1f&#125; KB)&quot;)if __name__ == &quot;__main__&quot;:    main()


